let slides=document.querySelector(".slide"),slide=document.querySelectorAll(".slide li"),slideCount=slide.length,currentIdx=0,slideWidth=300,slideMargin=30,prev=document.querySelector(".prev_btn"),next=document.querySelector(".next_btn");function moveSlide(e){slides.style.left=330*-e+"px",currentIdx=e}slides.style.width=(slideWidth+slideMargin)*slideCount-slideMargin+"px",next.addEventListener("click",()=>{moveSlide(currentIdx<slideCount-6?currentIdx+1:0)}),prev.addEventListener("click",()=>{moveSlide(currentIdx>0?currentIdx-1:slideCount-3)});