let slides=document.querySelector(".slides"),slide=document.querySelectorAll(".slides li"),slideCount=slide.length,slideWidth=300,slideMargin=30,currentIdx=0,prev=document.querySelector(".prev__btn"),next=document.querySelector(".next__btn");function moveSlide(e){slides.style.left=330*-e+"px",currentIdx=e}slides.style.width=(slideWidth+slideMargin)*slideCount-slideMargin+"px",next.addEventListener("click",()=>{moveSlide(currentIdx<slideCount-6?currentIdx+1:0)}),prev.addEventListener("click",()=>{moveSlide(currentIdx>0?currentIdx-1:slideCount-6)});